sounds.sounds:
instrument tin 3 {
    notes [A2-A4] >F
    wave tin 3 {
        dur 1
        freq F> >a
        decay 3 >b
        saw a> >c1
        sin a> >c2
        mix c1> c2> b> >d
        amp d> 0.7 >tin
    }


}
wave sin 3 { dur 1; freq 440 >F; sin F> >a; amp a> 0.7 >sin; }
wave tri 3 { dur 1; freq 440 >F; tri F> >a; amp a> 0.7 >tri;}
wave saw 3 { dur 1; freq 440 >F; saw F> >a; amp a> 0.7 >saw;}
wave sqr 3 { dur 1; freq 440 >F; sqr F> >a; amp a> 0.7 >sqr;}
wave pulse 3 { dur 1; freq 440 >F; pulse F> 0.2 >a; amp a> 0.7 >pulse;}
wave noise 3 { dur 1; freq 440 >F; noise F> >a; amp a> 0.7 >noise;}
wave triDelay 3 {
    dur 1;
    freq 440 >F
    tri F> >a
    rampOn 0.002 >r
    amp a> r> >a1
    bufDelay 0.1 a1> >b
    mix a1> b> 0.5 >c
    bufDelay 0.13 c> >d
    mix c> d> 0.5 >e
    amp e> 0.7 >triDelay
}

wave mixedCh1 3 {
    dur 1
    freq 440 >F
    sin F> >wa
    saw F> >wb
    decay 0.5 >da
    mix wa> wb> da> >a
    chase 0.005 220 a> >b
    amp b> 0.7 >mixedCh1
}
wave mixedCh2 3 {
    dur 1
    freq 440 >F
    sin F> >wa
    saw F> >wb
    decay 0.5 >da
    mix wa> wb> da> >a
    chase 0.007 880 a> >b
    amp b> 0.7 >mixedCh2
}
wave mixed 3 {
    dur 1
    freq 440 >F
    sin F> >wa
    saw F> >wb
    decay 0.05 >da
    mix wa> wb> da> >a
    runMean 0.01 a> >b
    amp b> 0.7 >mixed
}
